package org.example.taskservice.security;


import lombok.extern.slf4j.Slf4j;
import org.example.taskservice.dto.RegisterReq;
import org.example.taskservice.dto.Role;
import org.example.taskservice.entity.UserEntity;
import org.example.taskservice.exception.ElemNotFound;
import org.example.taskservice.repository.UserRepository;
import org.example.taskservice.utils.Encoder;
import org.springframework.security.core.GrantedAuthority;
import org.springframework.security.core.authority.SimpleGrantedAuthority;
import org.springframework.security.core.userdetails.User;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.security.core.userdetails.UserDetailsService;
import org.springframework.stereotype.Service;

import java.util.ArrayList;
import java.util.List;

@Service("UserDetailServiceImpl")
@Slf4j
public class UserDetailService implements UserDetailsService {

  private final UserRepository userRepository;

  public UserDetailService(UserRepository userRepository) {
    this.userRepository = userRepository;
  }

  /**
   * Аутентификация пользователя
   */
  public boolean login(String email, String password) {
    log.info("login");
    UserEntity userEntity = userRepository.findByEmail(email).orElse(null);
    if (userEntity == null) {
      return false;
    }
    String encryptedPassword = userEntity.getPassword();
    return Encoder.passwordEncoderWithBCrypt().matches(password, encryptedPassword);
  }

  /**
   * Сетим поля ентити из запроса для сохранения в таблицу, проверяем на нуль
   */
  public boolean register(RegisterReq registerReq) {
    log.info("register");
    UserEntity userEntity = userRepository.findByEmail(registerReq.getUsername()).orElse(null);
    if (userEntity != null) {
      return false;
    }
    userEntity = new UserEntity();
    userEntity.setName(registerReq.getName());
    userEntity.setEmail(registerReq.getUsername());
    userEntity.setImage(
        "");
    userEntity.setPassword(Encoder.passwordEncoderWithBCrypt().encode(registerReq.getPassword()));
    userEntity.setRole(Role.USER);
    userRepository.save(userEntity);
    return true;
  }

  /**
   * передаем пароль и почту для настройки {@link org.example.taskservice.WebSecurityConfig #daoAuthenticationProvider() }
   */
  public UserDetails loadUserByUsername(String email) {
    UserEntity userEntity = userRepository.findByEmail(email)
        .orElseThrow(ElemNotFound::new);
    List<GrantedAuthority> authorities = new ArrayList<GrantedAuthority>();

    authorities.add(new SimpleGrantedAuthority("ROLE_USER"));
    if (userEntity.getRole().equals(Role.ADMIN)) {
      authorities.add(new SimpleGrantedAuthority("ROLE_ADMIN"));
    }

    return new User(userEntity.getEmail(), userEntity.getPassword(), authorities);
  }


}
